<?phpclass auth extends CI_Model {    function __construct(){        parent::__construct();    }	    function login($user,$pass) {		$sql = "SELECT * FROM sys_user WHERE username='$user'";		$query = $this->db->query($sql);				if($query->result() == array()){			return false;		}else{			foreach($query->result() as $row){				if($row->password == md5($pass)){										$data = array(						'login'				=> true,						'id'				=> $row->id,						'username'			=> $row->username,						'group'				=> $row->group_id,						'nama'				=> $row->nama,					);					$this->session->set_userdata($data);					return true;				}				return false;			}		}		return false;	}	function userLogin(){		return $this->db->get_where('sys_user',array('id'=>$this->session->userdata('id')))->row_array();	}	function parent_menu(){		$group_id = $this->session->userdata('group');		$sql="select * from sys_menu 			where parent_id=0 and id in 			(select menu_id from sys_user_group where group_id='$group_id') order by urutan";		$result = $this->db->query($sql)->result_array();		return $result;	}		function child_menu(){		$group_id = $this->session->userdata('group');		$sql="select * from sys_menu 			where parent_id > 0 and id in 			(select menu_id from sys_user_group where group_id=$group_id) order by urutan";		$result = $this->db->query($sql)->result_array();		return $result;	}	function list_menu($data,$parent_id,$level){		echo "<ul>";		foreach($data as $row){			if($row['PARENT_ID'] == $parent_id and $row['LEVEL'] == ($level+1)){				if($row['URL'] == '#'){					echo "<li data-options=\"state:'closed',attributes:'parent',iconCls:'$row[ICON]'\">";					echo " <span> $row[NAMA]</span>";					$this->list_menu($data,$row['ID'],$row['LEVEL']);					echo "</li>";				}else{					echo "<li data-options=\"iconCls:'$row[ICON]',attributes:'$row[URL]'\" onclick=\"t_menu('$row[URL]')\"> $row[NAMA]</li>";				}			}		}		echo "</ul>";	}	function pengguna(){		$where = array('id'=>$this->session->userdata('id'));		$this->db->where($where);		$result = $this->db->get('sys_user')->row();		return $result;	}	function profil(){		return $this->db->get('profil')->row();	}}?>